/** *  */package com.isoftstone.demo.cache.intercepter;import java.util.Map;import org.slf4j.Logger;import org.slf4j.LoggerFactory;/** * @author 姚丰利(lidey) * 创建时间：2012-12-3 下午2:36:32  */public class InformationCacheInterceptor extends BaseCacheInterceptor {		final Logger logger = LoggerFactory.getLogger(InformationCacheInterceptor.class);		/**	 * 	 * @param targetName	 * @param methodName	 * @param arguments	 * @return	 */	public String getCacheKey(String targetName, String methodName,              Object[] arguments) {          StringBuffer sb = new StringBuffer();          sb.append(targetName).append(".").append(methodName);          Map<String, Object> params=(Map<String, Object>)arguments[0];        String categoryId= params.get("categoryId").toString();        int size=Integer.valueOf(arguments[1].toString());        sb.append("_").append(categoryId);        sb.append("_").append(size);        return sb.toString();      }}