/** * */package com.isoftstone.demo.common.model;import java.io.Serializable;import java.util.ArrayList;import java.util.HashMap;import java.util.List;import java.util.Map;import javax.xml.bind.annotation.XmlRootElement;import org.apache.commons.lang3.StringUtils;/** * @author 姚丰利(lidey) *         创建时间：2012-11-14 下午4:07:25 */@XmlRootElement(name = "TreeBean")public class TreeBean implements Serializable {    /**     *     */    private static final long serialVersionUID = 1L;    private String id;    private String parent;    private String text;    private String icon;    private Map<String, Boolean> state = new HashMap<String, Boolean>();    private Map<String, String> li_attr = new HashMap<String, String>();    private Map<String, String> a_attr = new HashMap<String, String>();    private List<TreeBean> children = new ArrayList<TreeBean>();    public TreeBean() {        state = new HashMap<String, Boolean>();        state.put("opened", false);        state.put("disabled", false);        state.put("selected", false);    }    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public String getParent() {        if (StringUtils.isEmpty(parent))            return "#";        else            return parent;    }    public void setParent(String parent) {        this.parent = parent;    }    public String getText() {        return text;    }    public void setText(String text) {        this.text = text;    }    public String getIcon() {        return icon;    }    public void setIcon(String icon) {        this.icon = icon;    }    public Map<String, Boolean> getState() {        return state;    }    public void setState(Map<String, Boolean> state) {        this.state = state;    }    public Map<String, String> getLi_attr() {        return li_attr;    }    public void setLi_attr(Map<String, String> li_attr) {        this.li_attr = li_attr;    }    public void putLi_attr(String key, String value) {        li_attr.put(key, value);    }    public String getLi_attr(String key) {        return li_attr.get(key);    }    public Map<String, String> getA_attr() {        return a_attr;    }    public void setA_attr(Map<String, String> a_attr) {        this.a_attr = a_attr;    }    public void putA_attr(String key, String value) {        a_attr.put(key, value);    }    public String getA_attr(String key) {        return a_attr.get(key);    }    public void setOpened(boolean opened) {        state.put("opened", opened);    }    public void setDisabled(boolean disabled) {        state.put("disabled", disabled);    }    public void setSelected(boolean selected) {        state.put("selected", selected);    }    public List<TreeBean> getChildren() {        return children;    }    public void setChildren(List<TreeBean> children) {        this.children = children;    }    public void addChildren(TreeBean children) {        this.children.add(children);    }}